let gulp = require('gulp');
let cleanCSS = require('gulp-clean-css');
let concatCss = require('gulp-concat-css');
let minify = require('gulp-minify');
let concat = require('gulp-concat');

const taskMinifyGithubMarkdownCSS = 'minify-github-markdown.css';
const taskCopyAwesomeFont = 'copy-font-awesome-font';
const taskCopyAce = 'copy-ace';
const taskCopyVsizegripPNG = 'copy-VsizegripPNG';
const taskCopyJqueryContextMenuFont = 'copyJqueryContextMenuFont';

//copy fontawesome font
gulp.task(taskCopyAwesomeFont, () => {
	return gulp.src('flib/font-awesome/fonts/*.*')
		.pipe(gulp.dest('static/fonts'));
});
gulp.task(taskMinifyGithubMarkdownCSS, () => {
	return gulp.src('flib/github-markdown-css/github-markdown.css')
		.pipe(cleanCSS({ compatibility: 'ie8' }))
		.pipe(gulp.dest('static/css'));
});

gulp.task(taskCopyAce, () => {
	return gulp.src('flib/ace-builds/src-min-noconflict/**')
		.pipe(gulp.dest('static/js/ace'));
});

gulp.task(taskCopyVsizegripPNG, () => {
	return gulp.src('flib/jquery-resizable/assets/vsizegrip.png')
		.pipe(gulp.dest('static/img'));
});
gulp.task(taskCopyJqueryContextMenuFont, () => {
	return gulp.src('flib/jQuery-contextMenu/dist/font/**')
		.pipe(gulp.dest('static/css/font'));
});
gulp.task('default', [
	taskCopyAwesomeFont,
	taskMinifyGithubMarkdownCSS,
	taskCopyAce,
	taskCopyVsizegripPNG,
	taskCopyJqueryContextMenuFont
]);