package optgroup

type OptGroup struct {
	Label    string
	Children []Option
}
type Option struct {
	Value string
	Text  string
}

func NewOption(strs ...string) Option {
	if len(strs) == 1 {
		return Option{
			Value: strs[0],
			Text:  strs[0],
		}
	}
	return Option{
		Value: strs[0],
		Text:  strs[1],
	}
}
