package recordview

//Operate 是运算符
type Operate struct {
	Label string
	Value string
}

//GetOperates 返回常用的操运算符
func GetOftenOperates() []Operate {
	return []Operate{
		Operate{"等于", "="},
		Operate{"包含", "?"},
		Operate{"前缀", "?>"},
		Operate{"为空", "e"},
		Operate{"不等于", "!="},
		Operate{"大于", ">"},
		Operate{"大于等于", ">="},
		Operate{"小于", "<"},
		Operate{"小于等于", "<="},
		Operate{"不包含", "!?"},
		Operate{"非前缀", "!?>"},
		Operate{"后缀", "<?"},
		Operate{"非后缀", "!<?"},
		Operate{"在列表", "in"},
		Operate{"不在列表", "!in"},
		Operate{"正则", "~"},
		Operate{"非正则", "!~"},
		Operate{"非空", "!e"},
		Operate{"长度等于", "_"},
		Operate{"长度不等于", "!_"},
		Operate{"长度大于", "_>"},
		Operate{"长度小于", "_<"},
	}
}
