$(function () {
	$('#confirm-delete').on('show.bs.modal', function (e) {
		var name = $("#confirm-delete").data("name");
		var click = $("#confirm-delete").data("click");
		$(this).find(".modal-body").html("是否要删除 <strong>" + name + "</strong>?");
		var btnok = $(this).find('.btn-ok');
		btnok.data("name", name);//name 传递
		btnok.click(eval(click));
	});

});