function initSQLRun(sql){
	$(function () {

		var edtSql = ace.edit("edtSql");
		edtSql.setTheme("ace/theme/sqlserver");
		edtSql.getSession().setMode("ace/mode/sql");
		edtSql.$blockScrolling = Infinity;
		edtSql.setValue(sql);
		$("#btnSelect").click(function () {
			$("#cmdtype").val("select");
			$("#hidSql").val(edtSql.getValue());
			$("form").submit();
		});
		$("#btnExecute").click(function () {
			$("#cmdtype").val("execute");
			$("#hidSql").val(edtSql.getValue());
			$("form").submit();
		});

	});
}